  <script>
    $(function(){
      $('#card_no').change(function(){
        if(!$('#card_no').val().match(/^\d+/) || $('#card_no').val().length !=16)
        {
          $('#card_no').val('');
          $('#card_no_error').addClass('show');         
          $('#gotoPay').attr('disabled', true);
          $('#gotoPay').css('opacity','0.3');
        }else{
          $('#card_no_error').removeClass('show');
          $('#gotoPay').attr('disabled', false);
          $('#gotoPay').css('opacity','1');
        }
      });
      $('#card_month').change(function(){
        if(!$('#card_month').val().match(/^\d+/) || $('#card_month').val().length !=2 || $('#card_month').val()>12 || $('#card_month').val()<0)
        {
          $('#card_month').val('');
          $('#card_month_error').addClass('show');
          $('#gotoPay').attr('disabled', true);
          $('#gotoPay').css('opacity','0.3');
        }else{
          $('#card_month_error').removeClass('show');
          $('#gotoPay').attr('disabled', false);
          $('#gotoPay').css('opacity','1');
        }
      });
      $('#card_year').change(function(){      
       
        if(!$('#card_year').val().match(/^\d+/) || $('#card_year').val().length !=2 || $('#card_year').val()< <?php echo date("y");  ?> )
        {
          $('#card_year').val('');
          $('#card_year_error').addClass('show');
          $('#gotoPay').attr('disabled', true);
          $('#gotoPay').css('opacity','0.3');
        }else{
          $('#card_year_error').removeClass('show');
          $('#gotoPay').attr('disabled', false);
          $('#gotoPay').css('opacity','1');
        }
      });
      $('#card_safe').change(function(){
        if(!$('#card_safe').val().match(/^\d+/) || $('#card_safe').val().length !=3)
        {
          $('#card_safe').val('');
          $('#card_safe_error').addClass('show');
          $('#gotoPay').attr('disabled', true);
          $('#gotoPay').css('opacity','0.3');
        }else{
          $('#card_safe_error').removeClass('show');
          $('#gotoPay').attr('disabled', false);
          $('#gotoPay').css('opacity','1');
        }
      });
      
    });
  </script> 
